/*	
	https://basel.codes/2019 // PCD 2019
	+ why P5LIVE was created!
	@maxfrischknecht
	@yannmartins
	@teddavis
	modified Boid class to ES5
*/

let personaggini = []

function setup() {
	createCanvas(windowWidth, windowHeight)
	textAlign(CENTER, CENTER)
	var loopc = innerWidth / 100
	var brdr = 20
	personaggini = []
	for(var i = 0; i < loopc; i++) {
		personaggini.push(new Boid(random(brdr, width - brdr), random(brdr, height - brdr)))
	}

}

function draw() {
	background(0)
	for(let boid of personaggini) {
		boid.update()
		boid.proximity(personaggini)
		boid.edges()
		boid.show(personaggini)
	}
}

function mousePressed() {
	personaggini.push(new Boid(mouseX, mouseY))
}

function keyPressed() {
	if(keyCode == 8) {
		personaggini = []
	}

}


