	_audio_gen_tonejs // cc teddavis.org 2021

	add '// no p5sound' anywhere in sketch,
	to exclude p5.sound from loading (ie. for tone.js)
	* need to compile once to allow 'userGesture'

	(x) toggle jacks to set arrangement
*/


// no p5sound
let libs = ["https://unpkg.com/tone", "includes/libs/Tone.js"]
let synth // tone.js instance

let counter = 0,
	tempo = 10,
	notesCount = 6,
	notes = [],
	font, bounds

function preload() {
	font = loadFont("includes/demos-data/fonts/RobotoMono-Regular.otf")
}

function setup() {
	createCanvas(windowWidth, windowHeight);

	// tone.js synth
	// synth = new Tone.FMSynth().toDestination();

	// tone.js sampler
	synth = new Tone.Sampler({
		urls: {
			"C4": "includes/demos-data/audio/drum.mp3" // p5.js assets
		},
		release: 1,
	}).toDestination();

	// setup type
	genType('P5LIVE', width / 5)
}

function draw() {
	background(0, 10)

	// playback head
	stroke(255)
	strokeWeight(2)
	counter += noise(frameCount * .05) * 20
	let pos = counter % width
	line(pos, 0, pos, height)

	translate(width / 2, height / 2)

	// notes
	for(let n of notes) {
		noFill()
		stroke(255)
		strokeWeight(.5)
		if(n.play) {
			strokeWeight(3)
			stroke(50, 255, 0)

			// play note logic
			if(abs((n.x + width / 2) - pos) < tempo && !n.playing) {
				n.playing = true
				let pitch = map(n.y, -bounds.h / 2, bounds.h / 2, 80, 10)
				synth.triggerAttackRelease(pitch, .2);
				fill(255)
			} else {
				n.playing = false
			}

		}
		circle(n.x, n.y, 20)
	}


	// patch cable
	noFill()
	stroke(255)
	strokeWeight(4)
	let init = true,
		lastNode = null
	beginShape()
	for(let n of notes) {
		if(n.play) {
			if(init) {
				curveVertex(n.x, n.y)
				init = false
			}
			curveVertex(n.x, n.y)
			lastNode = createVector(n.x, n.y)
		}
	}
	if(lastNode !== null) {
		curveVertex(lastNode.x, lastNode.y)
	}
	endShape()
}

function mousePressed() {
	for(let n of notes) {
		if(dist(mouseX, mouseY, n.x + width / 2, n.y + height / 2) < 10) {
			n.play = !n.play
		}
	}
}

