	// genType(key, width / 4)
}

function genType(txtString, txtSize) {
	// grab bounding box of text
	bounds = font.textBounds(txtString, 0, 0, txtSize)

	// textToPoints(txt, x, y, size, options)
	notes = font.textToPoints(txtString, -bounds.w / 2, bounds.h / 2, txtSize, {
		sampleFactor: .03
	})

	// set play logic
	for(let n of notes) {
		n.play = false
		n.playing = false
		if(random(1) > .95) {
			n.play = true
		}
	}
}

