/*	
	_typo_opentype // cc teddavis.org 2021	
	isolated glyphs + opentype.js features
*/

let libs = ['https://cdn.jsdelivr.net/npm/opentype.js@latest/dist/opentype.min.js']

let fontPath = 'includes/demos-data/fonts/RobotoMono-Regular.otf'
let path

function setup() {
	createCanvas(windowWidth, windowHeight)
	noFill()

	genOpenType('P5LIVE', width / 4)
}

function draw() {
	background(0, 5)
	translate(width / 2, height / 2)

	let osc = sin(frameCount * .01) * 15

	// cycle path if ready
	if(path) {
		let c = 0
		for(let cmd of path.commands) {
			if(cmd.type === 'M') {
				// start of each glyph
				push()
				rotate(radians(osc))
				beginShape()
				vertex(cmd.x, cmd.y)
			} else if(cmd.type === 'L') {
				vertex(cmd.x, cmd.y)
			} else if(cmd.type === 'C') {
				bezierVertex(cmd.x1, cmd.y1, cmd.x2, cmd.y2, cmd.x, cmd.y)
			} else if(cmd.type === 'Q') {
				quadraticVertex(cmd.x1, cmd.y1, cmd.x, cmd.y)
			} else if(cmd.type === 'Z') {
				// end of each glyph
				stroke((c * 5 + frameCount) % 255);
				endShape(CLOSE)
				pop()
			}
			c++
		}
	}
}

function genOpenType(txtString, txtSize) {
	opentype.load(fontPath, function(err, f) {
		if(err) {
			print('Error loading font: ' + err)
		} else {
			// grab once for measuring
			path = f.getPath(txtString, 0, 0, txtSize)
			let bounds = path.getBoundingBox()

			// reposition to 0, 0 as center (for WEBGL, rotate, etc)
			path = f.getPath(txtString, -(bounds.x2 - bounds.x1) / 2, (bounds.y2 - bounds.y1) / 2, txtSize)
		}
	})
}

