	_hardward_gamepad // cc teddavis.org 2020
	demo (for PSX) uses browser's built-in gamepad api
*/

let gp, pos, dir, useBG = true,
	vel = 5,
	shapeMode = 0,
	sw = 1

function setup() {
	createCanvas(windowWidth, windowHeight)
	pos = createVector(width / 2, height / 2, 50)
	dir = createVector(0, 0, 0)
	rectMode(CENTER)
	noFill()
	stroke(255)
}

function draw() {
	if(useBG) {
		background(0, 25)
	}

	gpUpdate()
}

// gamepad connected
window.addEventListener("gamepadconnected", function() {
	gp = navigator.getGamepads()[0]
	console.log("gamepad connected!")
	console.log(gp)
});

// gamepad disconnected
window.addEventListener("gamepaddisconnected", function() {
	gp = undefined
});

function gpUpdate() {

	// draw forms
	strokeWeight(sw)
	switch (shapeMode) {
		case 0:
			rect(pos.x, pos.y, pos.z)
			break;
		case 1:
			line(pos.x - pos.z / 2, pos.y - pos.z / 2, pos.x + pos.z / 2, pos.y + pos.z / 2)
			line(pos.x + pos.z / 2, pos.y - pos.z / 2, pos.x - pos.z / 2, pos.y + pos.z / 2)
			break;
		case 2:
			ellipse(pos.x, pos.y, pos.z)
			break;
		case 3:
			triangle(pos.x, pos.y - pos.z / 2, pos.x + pos.z / 2, pos.y + pos.z / 2, pos.x - pos.z / 2, pos.y + pos.z / 2)
			break;
	}

	// receive axis + buttons presses
	if(gp !== undefined) {
		gp = navigator.getGamepads()[0]

		// buttons pressed
		for(let i = 0; i < gp.buttons.length; i++) {
			if(gp.buttons[i].pressed) {
				//	print(i)

				// change shape based on button
				if(i < 4) {
					shapeMode = i
				}
			}
		}



		// horizontal dpad axis
		if(gp.axes[0] == 1 || gp.axes[0] == -1) {
			dir.x += gp.axes[0] / 10
		}

		pos.x += dir.x * vel;
		if(pos.x > width + pos.z / 2) {
			pos.x = -pos.z / 2
		} else if(pos.x < -pos.z / 2) {
			pos.x = width + pos.z / 2
		}

		// vertical dpad axis
		if(gp.axes[1] == 1 || gp.axes[1] == -1) {
			dir.y += gp.axes[1] / 10
		}

		pos.y += dir.y * vel
		if(pos.y > height + pos.z / 2) {
			pos.y = -pos.z / 2
		} else if(pos.y < -pos.z / 2) {
			pos.y = height + pos.z / 2
		}

		// shoulder buttons (R1/L1)
		if(gp.buttons[5].pressed) {
			pos.z += vel
		} else if(gp.buttons[4].pressed) {
			if(pos.z > 15) {
				pos.z -= vel
			}
		}

		// shoulder triggers (R2/L2)
		if(gp.buttons[7].pressed) {
			sw += 1
		} else if(gp.buttons[6].pressed) {
			if(sw > 1) {
				sw -= 1
			}
		}

		// start + select
		if(gp.buttons[9].pressed) {
			dir = createVector(0, 0, 0)
		} else if(gp.buttons[8].pressed) {
			useBG = !useBG
		}
	}
}

