let vertShader = `
	// getposition attribute of the geometry
	attribute vec3 aPosition;

	// get texture coordinate attribute from the geometry
	attribute vec2 aTexCoord;

	// get vertex normal attribute from the geometry
	attribute vec3 aNormal;

	// uProjectionMatrix to convert 3d world coords to screen coords 
	uniform mat4 uProjectionMatrix;

	// uModelViewMatrix combines model matrix and view matrix
	// model matrix defines object position / rotation / scale
	// view matrix defines camera focal length / position
	uniform mat4 uModelViewMatrix;

	// normalmatrix is the transpose-inverse of the modelview matrix. 
	// It's currently broken in p5, but normally used in these calculations
	uniform mat4 uNormalMatrix;

	// pass the normal and the eye to the fragment shader
	varying vec3 vNormal;
	varying vec3 vEye;

	void main() {
		// calculate world space eye position and world space normal
		vEye = normalize( vec3(uModelViewMatrix * vec4(aPosition, 1.0)));

		// typically use uNormalMatrix instead of uModelViewMatrix but currently there is a bug
		vNormal = normalize((uModelViewMatrix * vec4(aNormal, 0.0)).xyz);

		// copy position data into a vec4, using 1.0 as the w component
		vec4 positionVec4 = vec4(aPosition, 1.0);

		// move our vertex positions into screen space
		// order of multiplication is always projection * view * model * position
		// (model and view) have been combined so we just do projection * modelView * position
		gl_Position = uProjectionMatrix * uModelViewMatrix * positionVec4;
	}
`


let fragShader = `
	precision mediump float;

	// get normal from the vertex shader
	varying vec3 vNormal;
	varying vec3 vEye;

	uniform sampler2D uMatcapTexture;

	// returns texture coordinates. 
	// input eye position and normal, returns how a sphere reflects the texture. 
	// implementation borrowed from https://www.clicktorelease.com/blog/creating-spherical-environment-mapping-shader/
	vec2 matcap(vec3 eye, vec3 normal) {
		vec3 reflected = reflect(eye, normal);
		float m = 2.8284271247461903 * sqrt( reflected.z+1.0 );
		return reflected.xy / m + 0.5;
	}

	void main() {
		// calculate our uv
		vec2 uv = matcap(vEye, vNormal) ;

		// sample the texture
		vec4 color = texture2D(uMatcapTexture, uv);

		// draw the texcoords to the screen
		gl_FragColor = color;
	}
`

