let userID = 0; // use unique ID per user to separate multiple syncs


/* 1 - SYNC DATA */
let libs = ['https://www.auduno.com/clmtrackr/build/clmtrackr.min.js']
let capture, tracker;
let obj = {
	type: 'facetracker',
	id: userID,
	w: 320,
	h: 240,
	scl: 1,
	col: '#' + Math.floor(Math.random() * 16777215).toString(16),
	positions: [],
};

setupTracker();

function setupTracker() {
	capture = createCapture({
		audio: false,
		video: {
			width: obj.w,
			height: obj.h
		}
	}, function() {
		console.log('capture ready.')
	});
	capture.size(obj.w, obj.h);
	capture.hide();
	tracker = new clm.tracker();
	tracker.init();
	tracker.start(capture.elt);
	obj.scl = width / obj.w;
}

// send regular pulse of positions
setInterval(function() {
	if(tracker) {
		obj.positions = tracker.getCurrentPosition();
		sendData(obj); // user data
	}
}, 50);

