/* syncData Preset : midi
Transmit midi notes/pitchbend/controlchanges to all users.
Check console below after pressing '► RUN' to learn device IDs + Names 

0 - PREFS (unique local settings)
1 - SYNC DATA (active in Sync Data)
2 - COCODE (add to COCODING session)
*/

/* 0 - PREFS */
let midiUser = 0; // <-- give yourself unique ID for forwarding midi
let midiDeviceIn = 0; // [ID] or "device name"
let midiDeviceOut = 0; // [ID] or "device name"
let midiThru = false; // pass all midi data in -> out (except self)

// debug latency
let localLatency = false; // true » check latency of own midi signal
let getLatency = false; // true » check latency of roundtrip (if peers sendLatency)
let sendLatency = false; // only peers should activate for sending back

/* 1 - SYNC DATA */
let libs = ["includes/js/webmidi.min.js"];
let midiInput, midiOutput;

// part of p5live.ccoding.js
setupMidi(midiDeviceIn, midiDeviceOut); // deviceIn, deviceOut

function parseMidi(mm) {
	mm.type = 'midi'; // set syncData type
	mm.user = midiUser; // set unique person ID
	sendData(mm); // send to peers
}

function getData(obj) {
	// parse forwarded midiMessages from others to your local gear!
	if(obj.type && obj.type == 'midi') {
		if((obj.user != midiUser || midiDeviceIn != midiDeviceOut) && midiThru) {
			let mm = obj;

			// debug local latency in ms
			if(localLatency) {
				let lag = new Date().getTime() - mm.start;
				print(lag);
			}

			// peers sending signal back to origin for roundtrip debug
			if(sendLatency) {
				let pingpong = {
					type: 'latency',
					start: mm.start
				};
				sendData(pingpong);
			}

			// midiThru
			if(mm.data[2]) {
				midiOutput.send(mm.data[0], [mm.data[1], mm.data[2]]);
			} else {
				midiOutput.send(mm.data[0]);
			}
		}
	} else if(obj.type && obj.type == 'latency') {

		// debug roundtrip signal in ms
		if(getLatency) {
			println(new Date().getTime() - obj.start);
		}
	}
}


/* 2 - COCODE */
let midiMsg = {}; // check if props defined, useful in draw()

function parseData(obj) {
	// print(obj) // debug incoming message

	// parse only 'midi' data
	if(obj.type && obj.type == 'midi') {
		let mm = obj; // midimessage
		if(mm.note !== undefined) {
			switch (mm.note.type) {
				case 'noteon':
					noteOn(mm.note);
					break;
				case 'noteoff':
					noteOff(mm.note);
					break;
			}
		} else if(mm.pitch !== undefined) {
			pitchBend(mm.pitch);
		} else if(mm.control !== undefined) {
			controlChange(mm.contol);
		}

		// global var for use in draw()
		midiMsg = mm; // get latest message

		// optionally listen to midiMessages from others on your local gear!
		getData(obj); // ignored by those without syncData active
	} else if(obj.type && obj.type == 'latency') {
		getData(obj);
	}
}

